    }
	@keyframes blinkingText{
		0%		{ color: #000; background-color: #c0000000; border: 1px solid #000;}
		25%		{ color: #F00; background-color: #FFFF00; border: 1px solid #F00;}
		50%		{ color: #F00; background-color: #FFFF00; border: 1px solid #F00;} 
