/* RADIAL MENU - Move to Left side of Screen */
/* 
Button 1 is the Edit Token menu button
Button 2 is the Status Marker menu button
Button 3 is Bar 1 red HP
        top: -25px;
Button 4 is Bar 2 blue AC
        top: 15px;
Button 5 is Bar 3 green Speed
        top: 55px;
*/
#radial-menu {
        --color-text-primary: #b3b3b3;
        --color-bg-dark: #1c1111;
        --color-text-disabled: #150d0d;
        --color-element-bg: #372828;
        --color-red: #f85212;
        --color-green: #78ff10;
        --color-blue: #0c6be7;
        --color-yellow: #fc0;
        --font-number: "Ruda", sans-serif;
}
#radial-menu {
		position: fixed;
		top: 45px !important;
		left: 5px !important;
} /* Bar Buttons to left side */
#radial-menu .button {
        background: var(--color-element-bg) !important; 
        border: 0px; 
} /* Removes white outline from buttons */
#radial-menu .button.button-1 {
        border-radius: 25px 25px 25px 25px; /* Round Edit Token menu button */
        display: none !important; /* Remove Edit Token menu button */
}
#radial-menu .button.button-2 {
        border-radius: 25px 25px 25px 25px; 
} /* Round Status Marker menu button */
#radial-menu .button.button-1,
#radial-menu .button.button-2 {
        box-shadow: 0px 0px 5px var(--color-text-disabled);
        left: 0px;
} /* Shadow for Edit Token & Status Marker buttons */
#radial-menu .button.button-1,
#radial-menu .button.button-2 {
        position:fixed;
        left:5px !important;
} /* Horizontal placement for Edit Token & Status Marker buttons */
#radial-menu .button.button-2 {
        top:123px;
} /* Vertical placement for Status Marker button */
#radial-menu .button.button-1 {
        top: 218px;
} /* Vertical placement for Edit Token button */
#radial-menu .button.button-3,
#radial-menu .button.button-4,
#radial-menu .button.button-5 {
        left: -45px; 
} /* Horizontal placement for bar buttons */
#radial-menu .button.button-3 {
        box-shadow: 0px 0px 5px var(--color-text-disabled),
            inset 0px 0px 5px var(--color-red),
            inset 0px 0px 10px var(--color-red),
            inset 0px 0px 15px var(--color-red) !important;
        top: -25px;
} /* Shadow for green bar button */
#radial-menu .button.button-4 {
        box-shadow: 0px 0px 5px var(--color-text-disabled),
            inset 0px 0px 5px var(--color-blue),
            inset 0px 0px 10px var(--color-blue),
            inset 0px 0px 15px var(--color-blue) !important;
        top: 15px;
} /* Shadow for blue bar button */
#radial-menu .button.button-5 {
        box-shadow: 0px 0px 5px var(--color-text-disabled),
            inset 0px 0px 5px var(--color-green),
            inset 0px 0px 10px var(--color-green),
            inset 0px 0px 15px var(--color-green) !important;
        top: 55px;
} /* Shadow for red bar button */
#radial-menu .button.button-1 div.inner,
#radial-menu .button.button-2 div.inner {
        margin-top: 4px;
} /* Vertical placement for Status Marker button shadow */
#radial-menu .button div.inner span {
        font-size: 18px !important;
} /* Font size for buttons */
#radial-menu .popupvalue {
        position: absolute;
        top: 5px;
        left: 45px;
        width: 55px;
        height: 30px;
        z-index: 1005
}
#radial-menu .popupvalue input {
        width: 100%;
        font-size: 16px
}



