/* RADIAL MENU - Token Marker Labels */
#radial-menu .markermenu.open {
        width: 230px;
        min-width: 230px;
        min-height: 420px; 
        border-radius: 15px;
}
/* Prevent the icons from taking up extra space, allow space for the label text */
#radial-menu .markermenu.open .statusicon {
    font-size:0;
    margin-bottom: 1px;
}
#radial-menu .markermenu.open .statusicon,
#radial-menu .markermenu .markericon{
    height: 34px;
    width: 34px;
    background-size: 34px;
}
/* Undo the default dead "X" styling */
#radial-menu .markermenu .markercolor.dead {
    font-weight: normal;
    height: 34px;
    margin-left: 0;
    padding: 0;
    top: 0 !important;
    width: 34px;
}
/* Show the icon titles as labels */
#radial-menu .markermenu.open .statusicon::after {
    color: black;
    content: attr(title);
    display: inline-block;
    font-size: 9px;
    line-height: 1em;
    overflow: hidden;
    position: relative;
    text-align: center;
    top: 34px;
    white-space: nowrap;
    width: 34px;
}
/* Recreate the dead "X" without disrupting the layout */
#radial-menu .markermenu .markercolor.dead::before {
    content: "X";
    display: block;
    font-size: 34px;
    font-weight: bold;
    position: absolute;
    text-align: center;
    top: 7px;
    width: 100%;
}
